mapper.select:
select b.b_articleNo, b.b_parentNo, b.b_title, b.b_content, b.b_writeDate , b.b_field, b.userkey, b.b_imageFile , a.nickName 
	from Board b,
 	ALLMEMBER a 
	where b.userkey = a.userkey
	and b_articleNo = #{b_aritcleNo }

mapper.update:
	update Board set
	b_title = #{b_title },
	b_content = #{b_content }


	,b_imageFile = #{b_imageFile }

	where b_articleNo = #{b_articleNo }

