mapper.select:
select b.b_articleNo, b.b_key, b.b_parentNo, b.b_title, b.b_content, b.b_writeDate ,b.b_view, b.b_field, b.userkey, b.b_imageFile , a.nickName 
	from Board b,
 	ALLMEMBER a 
		where b.userkey = a.userkey
		and b_articleNo = #{b_aritcleNo }

mapper.update:
	update Board set
	b_title = #{b_title },
	b_content = #{b_content }

	,b_imageFile = #{b_imageFile }

	where b_articleNo = #{b_articleNo }

mapper.select:
select b.b_articleNo, b.b_parentNo, b.b_title, b.b_content, b.b_writeDate , b.userkey, b.b_articlePwd, b.b_imageFile , a.nickName
from Board b,
ALLMEMBER a
where b.userkey = a.userkey
and b_articleNo = ${_parameter }

mapper.update:
update Board set
b_view = #{b_view}
where b_articleNo = #{b_articleNo}

mapper.select:
select * from(
	select rownum as rnum, tmp.* from(
	select * from board 
	order by b_writedate desc
	) tmp
	)
	where (#{pageNum-1)- * #{amount } < rnum 
	and rnum <= ${pageNum } * ${amount }

mapper.update:
update board_com set
b_c_comment = #{b_c_comment }
where b_c_key = #{b_c_key}
